mapper.select:
 		select count(*) from
		review_post
		where id=#{value}

 		select r.rp_no,r.rp_title,r.rp_regdate from(
		select row_number() over (order by rp_no desc) as rnum,rp_no,rp_title,to_char(rp_regdate,'YYYY,MM,DD') as rp_regdate,id
		from REVIEW_POST
		where id=#{id}
		) r, GT_MEMBER m
		where r.id=m.id and rnum between #{startRowNumber} and #{endRowNumber}
		order by r.rp_no desc

select rnum, wq_no as wqNo, wq_title as wqTitle, wq_contents as wqContents, wq_regdate as wqRegdate, 
wq_parent_no as wqParentNo, wq_status as wqStatus
from (select row_number() over(order by wq_no desc) as rnum, 
wq_no, wq_title, wq_contents, to_char(wq_regdate, 'YYYY.MM.DD') as wq_regdate, wq_parent_no, wq_status
from web_question where id = #{id})
where rnum between #{startRowNumber} and #{endRowNumber}

select wq_no as wqNo, wq_title as wqTitle, wq_contents as wqContents, 
to_char(wq_regdate, 'YYYY.MM.DD') as wqRegdate, wq_status as wqStatus
from web_question where wq_no=#{value}
